    public void completeReq() {
        String id = jLabel4.getText();
        String price = jTextField1.getText();
        Date date = jDateChooser2.getDate();

        if (price.isEmpty()) {
            JOptionPane.showMessageDialog(this, "Please enter price", "Warning", JOptionPane.WARNING_MESSAGE);
        } else if (date == null) {
            JOptionPane.showMessageDialog(this, "Please select fixed date", "Warning", JOptionPane.WARNING_MESSAGE);
        } else {

            MySQL.iud("UPDATE repair_req SET `repair_status_id`= '4',`fixed_date`='" + sdf.format(date) + "',`price`='" + price + "'\n"
                    + "WHERE id='" + id + "'");
            this.panel.loadRapirReq();

            this.dispose();
            JOptionPane.showMessageDialog(this, "Status chaged success", "Success", JOptionPane.INFORMATION_MESSAGE, StaticComponent.successIcon);

        }
    }

